const ItineraryCreatePage = () => {
    const navigate = useNavigate();
    const { showSnackbar } = useSnackbar();
    const { addItinerary } = useItinerary();
    const [loading, setLoading] = useState(false);
    const [formData, setFormData] = useState({
        destination: '',
        startDate: '',
        endDate: '',
        travelers: '1',
        budget: '',
        interests: [],
        notes: ''
    });

    const interestOptions = [
        'Adventure', 'Culture', 'Food', 'Nature', 'History',
        'Beach', 'Mountains', 'Cities', 'Shopping', 'Relaxation'
    ];

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
    };

    const handleInterestToggle = (interest) => {
        setFormData(prev => ({
            ...prev,
            interests: prev.interests.includes(interest)
                ? prev.interests.filter(i => i !== interest)
                : [...prev.interests, interest]
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        setLoading(true);

        // Validate form
        if (!formData.destination || !formData.startDate || !formData.endDate || !formData.budget) {
            showSnackbar('Please fill in all required fields', 'error');
            setLoading(false);
            return;
        }

        if (formData.interests.length === 0) {
            showSnackbar('Please select at least one interest', 'error');
            setLoading(false);
            return;
        }

        // Simulate API call
        setTimeout(() => {
            console.log('Form submitted:', formData);

            // Add to context
            addItinerary(formData);

            showSnackbar('Itinerary created successfully!', 'success');
            setLoading(false);

            // Reset form
            setFormData({
                destination: '',
                startDate: '',
                endDate: '',
                travelers: '1',
                budget: '',
                interests: [],
                notes: ''
            });

            // Navigate after a short delay
            setTimeout(() => {
                navigate('/itineraries');
            }, 500);
        }, 1500);
    };

    return (
        <Container maxWidth="md">
            <Box sx={{ py: 4 }}>
                <Button
                    startIcon={<ArrowBackIcon />}
                    onClick={() => navigate('/itineraries')}
                    sx={{ mb: 3 }}
                >
                    Back to Itineraries
                </Button>

                <Typography variant="h4" gutterBottom>Create New Itinerary</Typography>
                <Typography variant="body2" color="text.secondary" sx={{ mb: 3 }}>
                    Plan your perfect trip by providing these details
                </Typography>

                <Card sx={{ p: 3 }}>
                    <form onSubmit={handleSubmit}>
                        <Grid container spacing={3}>
                            {/* Destination */}
                            <Grid item xs={12}>
                                <TextField
                                    fullWidth
                                    label="Destination *"
                                    placeholder="e.g., Paris, Tokyo, New York"
                                    name="destination"
                                    value={formData.destination}
                                    onChange={handleInputChange}
                                    required
                                />
                            </Grid>

                            {/* Travel Dates */}
                            <Grid item xs={12} sm={6}>
                                <TextField
                                    fullWidth
                                    label="Start Date *"
                                    name="startDate"
                                    type="date"
                                    value={formData.startDate}
                                    onChange={handleInputChange}
                                    InputLabelProps={{ shrink: true }}
                                    required
                                />
                            </Grid>
                            <Grid item xs={12} sm={6}>
                                <TextField
                                    fullWidth
                                    label="End Date *"
                                    name="endDate"
                                    type="date"
                                    value={formData.endDate}
                                    onChange={handleInputChange}
                                    InputLabelProps={{ shrink: true }}
                                    required
                                />
                            </Grid>

                            {/* Travelers & Budget */}
                            <Grid item xs={12} sm={6}>
                                <TextField
                                    fullWidth
                                    label="Number of Travelers"
                                    name="travelers"
                                    type="number"
                                    value={formData.travelers}
                                    onChange={handleInputChange}
                                    inputProps={{ min: 1 }}
                                />
                            </Grid>
                            <Grid item xs={12} sm={6}>
                                <TextField
                                    fullWidth
                                    label="Budget (USD) *"
                                    placeholder="e.g., 5000"
                                    name="budget"
                                    type="number"
                                    value={formData.budget}
                                    onChange={handleInputChange}
                                    inputProps={{ min: 0 }}
                                    required
                                />
                            </Grid>

                            {/* Interests */}
                            <Grid item xs={12}>
                                <Typography variant="subtitle2" sx={{ mb: 2 }}>
                                    Interests (Select at least one)
                                </Typography>
                                <Box sx={{ display: 'flex', gap: 1, flexWrap: 'wrap' }}>
                                    {interestOptions.map(interest => (
                                        <Chip
                                            key={interest}
                                            label={interest}
                                            onClick={() => handleInterestToggle(interest)}
                                            color={formData.interests.includes(interest) ? 'primary' : 'default'}
                                            variant={formData.interests.includes(interest) ? 'filled' : 'outlined'}
                                        />
                                    ))}
                                </Box>
                            </Grid>

                            {/* Notes */}
                            <Grid item xs={12}>
                                <TextField
                                    fullWidth
                                    label="Additional Notes"
                                    placeholder="Any special requests or preferences..."
                                    name="notes"
                                    value={formData.notes}
                                    onChange={handleInputChange}
                                    multiline
                                    rows={4}
                                />
                            </Grid>

                            {/* Buttons */}
                            <Grid item xs={12} sx={{ display: 'flex', gap: 2, justifyContent: 'flex-end' }}>
                                <Button
                                    variant="outlined"
                                    onClick={() => navigate('/itineraries')}
                                >
                                    Cancel
                                </Button>
                                <Button
                                    variant="contained"
                                    type="submit"
                                    disabled={loading}
                                >
                                    {loading ? 'Creating...' : 'Create Itinerary'}
                                </Button>
                            </Grid>
                        </Grid>
                    </form>
                </Card>
            </Box>
        </Container>
    );
};

