// Mock weather data - replace with actual API calls to OpenWeatherMap/WeatherAPI
export const fetchWeatherForecast = async (destination, startDate, endDate) => {
    try {
        // In production, call: https://api.openweathermap.org/data/2.5/forecast?q={destination}&appid={API_KEY}
        // For now, return mock data for demo purposes

        const mockWeather = {
            destination,
            startDate,
            endDate,
            forecast: generateMockForecast(startDate, endDate),
            summary: {
                avgTemp: 28,
                condition: 'Partly Cloudy',
                precipitation: '20%',
                windSpeed: '10 km/h'
            }
        };

        return mockWeather;
    } catch (error) {
        console.error('Weather fetch error:', error);
        throw error;
    }
};

const generateMockForecast = (startDate, endDate) => {
    const forecast = [];
    const start = new Date(startDate);
    const end = new Date(endDate);

    const conditions = ['Sunny', 'Cloudy', 'Rainy', 'Partly Cloudy', 'Overcast'];

    for (let d = new Date(start); d <= end; d.setDate(d.getDate() + 1)) {
        forecast.push({
            date: new Date(d).toISOString().split('T')[0],
            temp: Math.floor(Math.random() * 15 + 20), // 20-35°C
            condition: conditions[Math.floor(Math.random() * conditions.length)],
            humidity: Math.floor(Math.random() * 40 + 40), // 40-80%
            windSpeed: Math.floor(Math.random() * 20 + 5), // 5-25 km/h
            precipitation: Math.floor(Math.random() * 100) // 0-100%
        });
    }

    return forecast;
};

export const getWeatherRecommendations = (forecast) => {
    const recommendations = [];

    forecast.forEach(day => {
        if (day.condition === 'Rainy' || day.precipitation > 60) {
            recommendations.push(`${day.date}: Carry umbrella and waterproof bag`);
        }
        if (day.temp > 30) {
            recommendations.push(`${day.date}: Use sunscreen and stay hydrated`);
        }
        if (day.windSpeed > 20) {
            recommendations.push(`${day.date}: Secure loose items, windy conditions`);
        }
    });

    return recommendations;
};
